{
  "name": "Modern Professional Template",
  "description": "Clean and professional CV template with advanced customization",
  "category": "professional",
  "layout": {
    "sections": ["header", "summary", "experience", "education", "skills"],
    "sectionOrder": ["header", "summary", "experience", "education", "skills"],
    "orientation": "portrait",
    "columns": 1,
    "sectionSpacing": 16
  },
  "typography": {
    "fontFamily": "Helvetica",
    "headingSize": 18,
    "subheadingSize": 14,
    "bodySize": 11,
    "lineHeight": 1.5,
    "fontWeight": "normal"
  },
  "theme": {
    "primaryColor": "#0066CC",
    "secondaryColor": "#666666",
    "accentColor": "#FF6600",
    "backgroundColor": "#FFFFFF",
    "textColor": "#000000",
    "customCSS": ""
  },
  "pageSettings": {
    "format": "A4",
    "margins": {
      "top": 20,
      "bottom": 20,
      "left": 20,
      "right": 20
    },
    "showLineBreaks": false,
    "showPageNumbers": true,
    "pageNumberFormat": "1",
    "isPublic": false
  },
  "exportSettings": {
    "pdf": {
      "enabled": true,
      "quality": "high",
      "watermark": ""
    },
    "json": {
      "enabled": true,
      "includeMetadata": false
    }
  },
  "notes": {
    "content": "Target: Senior Software Engineer role. Highlight React and Node.js experience.",
    "lastEditedAt": "2025-10-27T10:00:00Z"
  },
  "isPremium": false,
  "isActive": true
}

