{
  "name": "Custom Styled Template",
  "description": "Template with custom CSS styling",
  "category": "creative",
  "theme": {
    "primaryColor": "#FF6B6B",
    "secondaryColor": "#4ECDC4",
    "accentColor": "#FFE66D",
    "backgroundColor": "#F7F7F7",
    "textColor": "#2C3E50",
    "customCSS": ".header { border-bottom: 3px solid #FF6B6B; padding-bottom: 10px; } .section { margin-bottom: 20px; } .highlight { background-color: #FFE66D; padding: 2px 4px; }"
  },
  "layout": {
    "orientation": "portrait",
    "columns": 2,
    "sectionSpacing": 20
  },
  "pageSettings": {
    "format": "A4",
    "margins": {
      "top": 15,
      "bottom": 15,
      "left": 15,
      "right": 15
    }
  }
}

{
  "name": "Public Resume Template",
  "description": "Shareable public template",
  "category": "professional",
  "pageSettings": {
    "format": "A4",
    "isPublic": true,
    "publicUrl": "https://resume.example.com/john-doe"
  },
  "statistics": {
    "views": 0,
    "downloads": 0
  },
  "exportSettings": {
    "pdf": {
      "enabled": true,
      "quality": "high"
    },
    "json": {
      "enabled": true,
      "includeMetadata": true
    }
  }
}
